problems = json.loads(PROBLEMS_JSON)

# Group by file
files = {}
for p in problems:
    path = p['path']
    if os.path.exists(path):
        if path not in files:
            files[path] = []
        files[path].append(p)

for path, issues in files.items():
    with open(path, 'r', encoding='utf-8') as f:
        lines = f.readlines()
    
    # Process issues sorted by startLine DESC to check for 'const' messages
    # We will modify lines in place. indices are 0-based, issues are 1-based.
    
    modified = False
    
    # Filter only const issues
    const_issues = [i for i in issues if "Use 'const'" in i['message']]
    
    # Sort descending line to verify
    const_issues.sort(key=lambda x: x['startLine'], reverse=True)
    
    for issue in const_issues:
        line_idx = issue['startLine'] - 1
        if line_idx >= len(lines): continue
        
        line = lines[line_idx]
        
        # Check if already has const (simple check)
        if 'const ' in line:
            continue
            
        # Pattern matching for Constructors: ClassName(...) or named args like padding: ClassName(...)
        # We look for a Capitalized word followed by (
        # Exclude 'Key', 'String', etc if possible. But basically just find the FIRST constructor-like pattern?
        # Issues usually point to the START of the constructor.
        
        # Regex: Find word starting with Upper case, then (, possibly with spaces.
        # But wait, it might be `padding: EdgeInsets.all(16)`. `EdgeInsets` is valid.
        # `child: Column(`
        
        # We'll try to insert `const ` before the first match of `[A-Z]\w*` that is NOT preceded by `new ` (which doesn't exist really) or `const `.
        # And ensure we don't break things like `class Foo ...` or `String foo`.
        # Usually it's in a widget tree.
        
        # Match where: `child: <HERE>Container(` or `return <HERE>Container(` or `[ <HERE>Container(`
        
        # Regex to find insertion point:
        # Look for [A-Z]
        
        match = re.search(r'(?<!const\s)(?<!class\s)(?<!extends\s)(?<!implements\s)(\b[A-Z][a-zA-Z0-9_\.]*\s*\(?)', line)
        if match:
            # Check if it actually looks like a constructor (has "(" eventually?).
            # "Use 'const' with the constructor" is the hint.
            # If the line is just `Center(`, match is `Center(`. 
            # We replace `Center(` with `const Center(`.
            
            # Use span
            start, end = match.span()
            
            # Extra safety: Make sure it's not inside a string? Assume code is formatted.
            
            # Apply Edit
            new_line = line[:start] + 'const ' + line[start:]
            lines[line_idx] = new_line
            modified = True
            # print(f"Applied const at {path}:{issue['startLine']}: {line.strip()} -> {new_line.strip()}")

    if modified:
        with open(path, 'w', encoding='utf-8') as f:
            f.writelines(lines)
        print(f"Fixed consts in {path}")
